    if($('.min768').is(':visible')){
        $('.videoFromJs').prepend('<video muted loop id=\"mainVideo\" data-keepplaying poster=\"../images/mainBg.png\"><source src=\"/images/video.mp4\" type=\"video/mp4\"></video>');
        
