$(window).on('load', function(){
    setTimeout(function(){
        $('.modalStock').fadeIn(300);
    }, 4000);
});
   
$(document).ready(function () {
    
    var _top = 0;
    
    if($('.min768').is(':visible')){
      $('#mainVideo')[0].play();
    }
    
    if($('.full-page-definder').is(':visible')) {
    
        slider = $.fn.fsvs({
            speed : 800,
            bodyID : 'fsvs-body',
            selector : '> .section',
            mouseSwipeDisance : 40,
            afterSlide : function(){
               $('.active-slide .aos-init').addClass('aos-animate');
            },
            beforeSlide : function(){
               var ac_pag = $('.pagination-link.active').index(); 
               if(ac_pag != 0) {
                    if(!$('.fullpage_menu').hasClass('active')){
                        $('.fullpage_header').addClass('bgWhite');
                        $('.arrowDown').fadeOut();
                        $('.social').fadeOut();
                    }
               } else {
                   $('#mainVideo')[0].play();
                   if(!$('.fullpage_menu').hasClass('active')){
                        $('.fullpage_header').removeClass('bgWhite');
                        $('.arrowDown').fadeIn();
                        $('.social').fadeIn();
                    }
               }
            },
            endSlide : function(){
            
            },
            mouseWheelEvents : true,
            mouseWheelDelay : false,
            scrollableArea : '_normalScroll',
            mouseDragEvents : false,
            touchEvents : false,
            arrowKeyEvents : true,
            pagination : true,
            nthClasses : false,
            detectHash : true
        });
        
        // Arrow Down Click
        $(document).on('click', '.arrowDown', function(){
            slider.slideToIndex(1);
            $(this).fadeOut();
            $('.social').fadeOut();
            setTimeout(function(){
                $('.fullpage_header').addClass('bgWhite');
            }, 300);
        });
        
        // Arrow Top Click
        $('.scrollTop').click(function () {
            slider.slideToIndex(0);
            setTimeout(function(){
                $('#mainVideo')[0].play();
                $('.fullpage_header').removeClass('bgWhite');
                $('.arrowDown').fadeIn();
                $('.social').fadeIn();
            }, 300);
        });
        
         // Menu Bar on Click
        $('.menuBar').on('click', function(){
            var active = $('#fsvs-pagination .active').index();
            $(this).toggleClass('active');
            if(active != 0) {
                $('.fullpage_header').toggleClass('bgWhite');
                $('.fullpage_menu').fadeToggle().toggleClass('active');
                $('.section--1 h1').fadeToggle(800);
            }
            else {
                $('.fullpage_menu').fadeToggle().toggleClass('active');
                $('.section--1 h1').fadeToggle(800);
            }
            $('.arrowDown').fadeOut();
            $('.social').fadeOut();
        });
    } else {
    
        // Menu Bar on Click
        $('.menuBar').on('click', function(){
        
            if(!$('.fullpage_header').hasClass('sticky_header')){
                $('.fullpage_header').toggleClass('sticky_header');
            }
            $('.fullpage_menu').fadeToggle().toggleClass('active');
        });
    }  
     
    $(window).on('resize', function(){
        if($('.full-page-definder').is(':hidden')){
            $('.fullpage_header').removeClass('bgWhite');
            slider.unbind();
        } else {
            $('.fullpage_header').removeClass('sticky_header');
            slider.rebind();
        }
    });
        
    $('.last-section .scroll').on( 'DOMMouseScroll mousewheel', function ( event ) {
        if($('.full-page-definder').is(':visible')){
            var scrollT = $(this).scrollTop();
            if(scrollT <=0){          
                 _top++;
                if(_top>4){
                    slider.slideToIndex(3);
                    _top = 0;
                }
            }
        }
    });
});

";
$this->registerJs($script, yii\web\View::POS_END);

